                notice = "AL72_AGV_EC2_ALARM"
            break;
            case 8:
                notice = "AL73_AGV_EC3_ALARM"
            break;
            default:
        }
        flow.set("x", true);
        msg.payload = {
            Date : date,
            Time : time,
            Alarm : notice
        };
        return msg;
    } else {
        // Sudah dikirim sebelumnya, abaikan
        return null;
    }
} else if (signal === 0) {
    // Reset memory
    flow.set("x", false);
    return null;
} else {
    // Sinyal tidak dikenali
    return null;
}

